#include "SoundArtistMainWindow.h"SoundArtistMainWindow::SoundArtistMainWindow( wxWindow* parent ):MainWindow( parent ){    // Common Properties    m_pg->Append( new wxPropertyCategory(wxT("Common Properties")));    wxPGProperty* prop_start = m_pg->Append( new wxFloatProperty("StartTime", "Start (ms)", 0 ) );    prop_start->SetHelpString("The point in the file, in milliseconds, where the plots will begin.");    wxPGProperty* prop_end = m_pg->Append( new wxFloatProperty("EndTime", "End (ms)", 0 ) );    prop_end->SetHelpString("The point in the file, in milliseconds, where the plots will end.");    wxArrayString arrTimeAxis;    arrTimeAxis.Add("Seconds (s)");    arrTimeAxis.Add("Milliseconds (ms)");    m_pg->Append( new wxEnumProperty( "TimeAxis", "Time Axis", arrTimeAxis ));    // Waveform Properties    m_pg->Append( new wxPropertyCategory(wxT("Wave Form Properties")));    m_pg->Append( new wxBoolProperty("Show Y-Values") );    // Spectrogram Properties    m_pg->Append( new wxPropertyCategory(wxT("Spectrogram Properties")));    wxArrayString arrFreqAxis;    arrFreqAxis.Add("Hz");    arrFreqAxis.Add("kHz");    m_pg->Append( new wxEnumProperty( "FreqAxis", "Frequency Axis", arrFreqAxis ));}void SoundArtistMainWindow::OnOpen( wxCommandEvent& event ){	wxFileDialog* OpenDialog = new wxFileDialog(			this, "Select a WAV file to open", wxEmptyString, wxEmptyString,			"WAV Files (*.wav)|*.wav",			wxFD_OPEN, wxDefaultPosition);	if (OpenDialog->ShowModal() == wxID_OK)	{		wxString CurrentDocPath = OpenDialog->GetPath();		if (m_wr.openFile(CurrentDocPath))		{			SetTitle(wxString("SoundArtist - ") << OpenDialog->GetFilename());//			m_samplerateLabel->SetLabel(wxString::Format(wxT("%i"),m_wr.samplerate));		}	}	OpenDialog->Destroy();}void SoundArtistMainWindow::OnQuit( wxCommandEvent& event ){    Close(true);}